import {
  SET_CLASSIFICATIONS,
  SET_SELECTED_CLASSIFICATION,
  SET_SELECTED_VIDEO,
  SET_SUB_MENU,
  SET_VIDEO_TITLE,
} from '../actions/types';

const initialState = {
  subMenu: {},
  classifications: [],
  selectedVideo: {},
  selectedClassification: '',
  selectedVideoTitle: '',
};

const user = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case SET_SUB_MENU:
      return {
        ...state,
        subMenu: payload.data,
      };
    case SET_CLASSIFICATIONS:
      return {
        ...state,
        classifications: payload.data,
      };
    case SET_SELECTED_VIDEO:
      return {
        ...state,
        selectedVideo: payload,
      };
    case SET_SELECTED_CLASSIFICATION:
      return {
        ...state,
        selectedClassification: payload,
      };
    case SET_VIDEO_TITLE:
      return {
        ...state,
        selectedVideoTitle: payload,
      };
    default:
      return state;
  }
};

export default user;
